// App.js
import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [videos, setVideos] = useState([
    { id: 'video1', votes: 0 },
    { id: 'video2', votes: 0 },
    { id: 'video3', votes: 0 },
  ]);
  const [url, setUrl] = useState('');

  const handleInputChange = (e) => {
    setUrl(e.target.value);
  };

  const handleAddVideo = () => {
    if (url.trim() !== '') {
      const videoId = extractVideoId(url);
      if (videoId) {
        setVideos([...videos, { id: videoId, votes: 0 }]);
        setUrl('');
      }
    }
  };

  const handleRemoveVideo = (index) => {
    const updatedVideos = [...videos];
    updatedVideos.splice(index, 1);
    setVideos(updatedVideos);
  };

  const handleVote = (index, type) => {
    const updatedVideos = [...videos];
    if (type === 'up') {
      updatedVideos[index].votes += 1;
    } else if (type === 'down') {
      updatedVideos[index].votes -= 1;
    }
    setVideos(updatedVideos);
  };

  const extractVideoId = (url) => {
    const regex = /(?:youtu.be\/|youtube(?:-nocookie)?.com\/(?:embed\/|v\/|watch\?v=|watch\?.+&v=))([^?&\s]+)/;
    const match = url.match(regex);
    return match ? match[1] : null;
  };

  return (
    <div className="App">
      <h1>Video Player</h1>

      <div className="video-list-container">
        {videos.map((video, index) => (
          <div className="video-item" key={video.id}>
            <iframe
              title={`Video ${index + 1}`}
              width="560"
              height="315"
              src={`https://www.youtube.com/embed/${video.id}`}
              frameBorder="0"
              allowFullScreen
            ></iframe>
            <div className="video-actions">
              <button onClick={() => handleRemoveVideo(index)}>Remove</button>
              <button onClick={() => handleVote(index, 'up')}>Vote Up</button>
              <button onClick={() => handleVote(index, 'down')}>Vote Down</button>
              <span>Votes: {video.votes}</span>
            </div>
          </div>
        ))}
      </div>

      <div className="add-video-container">
        <input
          type="text"
          placeholder="Enter video URL"
          value={url}
          onChange={handleInputChange}
        />
        <button onClick={handleAddVideo}>Add Video</button>
      </div>
    </div>
  );
};

export default App;
