import React, { Component } from "react";
import VideoList from "./components/VideoList";
import VideoForm from "./components/VideoForm";

class App extends Component {
  state = {
    videoData: [
      {
        id: 1,
        title: "Video 1",
        url: "https://www.youtube.com/embed/dQw4w9WgXcQ",
        votes: 0,
      },
      {
        id: 2,
        title: "Video 2",
        url: "https://www.youtube.com/embed/3tmd-ClpJxA",
        votes: 0,
      },
    ],
    showForm: false,
  };

  handleVote = (id, increment) => {
    this.setState((prevState) => {
      const updatedVideoData = prevState.videoData.map((video) => {
        if (video.id === id) {
          return { ...video, votes: video.votes + increment };
        }
        return video;
      });

      return { videoData: updatedVideoData };
    });
  };

  handleRemove = (id) => {
    this.setState((prevState) => {
      const updatedVideoData = prevState.videoData.filter(
        (video) => video.id !== id
      );

      return { videoData: updatedVideoData };
    });
  };

  handleToggleForm = () => {
    this.setState((prevState) => {
      return { showForm: !prevState.showForm };
    });
  };

  handleAddVideo = (title, url) => {
    this.setState((prevState) => {
      const newVideo = {
        id: Date.now(),
        title,
        url,
        votes: 0,
      };

      const updatedVideoData = [...prevState.videoData, newVideo];

      return { videoData: updatedVideoData, showForm: false };
    });
  };

  render() {
    const { videoData, showForm } = this.state;

    return (
      <div className="app">
        <h1>Video Player</h1>
        <VideoList
          videoData={videoData}
          onVote={this.handleVote}
          onRemove={this.handleRemove}
        />
        <button className="add-video-button" onClick={this.handleToggleForm}>
          {showForm ? "Cancel" : "Add Video"}
        </button>
        {showForm && <VideoForm onAddVideo={this.handleAddVideo} />}
      </div>
    );
  }
}

export default App;
