import React, { useState } from "react";
import "../index.css";

const AddVideo = ({ onVidAdd }) => {
  const [title, setTitle] = useState("");
  const [url, setUrl] = useState("");

  const titleChange = (event) => {
    setTitle(event.target.value);
  };

  const urlChange = (event) => {
    setUrl(event.target.value);
  };

  // const newVid = () => {


  //   if (title !== "" && url !== "") {
  //     let vidObj={
  //       "title":`${title}`,
  //       "src":"${url}"
  //       }
  //       onVidAdd({...vidObj})
  //       } else{alert('please fill in all fields')}
  //       };

  //   }
  //     let embedUrl = url.replace("watch?v=", "embed/");

  //   const newInput = {
  //     title: title,
  //     url: embedUrl,
  //     rating:0

  //   };
  //   onVidAdd(newInput);
  //   setTitle("");
  //   setUrl("");
  // };
  const newVid = () => {
    if (title !== "" && url !== "") {
      let vidObj = {
        "title": `${title}`,
        "src": `${url}` // Fixed the syntax here
      };
      onVidAdd({ ...vidObj });
    } else {
      alert('please fill in all fields');
    }
  };

  let embedUrl = url.replace("watch?v=", "embed/");

  const newInput = {
    title: title,
    url: embedUrl,
    rating: 0
  };

  onVidAdd(newInput);
  setTitle("");
  setUrl("");


  return (
    <form onSubmit={newVid}>
      <div className="form-group row">
        <label htmlFor="colFormLabelSm" className="col-sm-2 col-form-label col-form-label-sm">
          Title
        </label>
        <div className="col-sm-10">
          <input
            type="text"
            value={title}
            onChange={titleChange}
            className="form-control form-control-sm"
            id="colFormLabelSm"
            placeholder="Title"
          />
        </div>
      </div>
      <div className="form-group row">
        <label htmlFor="colFormLabelSm" className="col-sm-2 col-form-label col-form-label-sm">
          URL
        </label>
        <div className="col-sm-10">
          <input
            type="text"
            value={url}
            onChange={urlChange}
            className="form-control form-control-sm"
            id="colFormLabelSm"
            placeholder="URL"
          />
        </div>
      </div>
      <button onClick={newVid} type="button" className="btn btn-success">
        {" "}
        Add{" "}
      </button>
      <button type="button" className="btn btn-warning">
        Cancel
      </button>
    </form>
  );
};

export default AddVideo;
