// App.js

import React, { Component } from "react";
import "./App.css";
import Video from "./components/Video";
import VideoList from "./components/VideoList";
import AddVideoForm from "./components/AddVideoForm";

class App extends Component {
  state = {
    videoData: [
      {
        id: 1,
        title: "Video 1",
        url: "https://www.youtube.com/embed/dQw4w9WgXcQ",
        votes: 0,
      },
      {
        id: 2,
        title: "Video 2",
        url: "https://www.youtube.com/embed/3tmd-ClpJxA",
        votes: 0,
      },
    ],
    showForm: false,
  };

  handleVote = (id, increment) => {
    this.setState((prevState) => ({
      videoData: prevState.videoData.map((video) => {
        if (video.id === id) {
          return {
            ...video,
            votes: video.votes + increment,
          };
        }
        return video;
      }),
    }));
  };

  handleAddVideo = (title, url) => {
    const newVideo = {
      id: Date.now(),
      title,
      url,
      votes: 0,
    };
    this.setState((prevState) => ({
      videoData: [...prevState.videoData, newVideo],
      showForm: false,
    }));
  };

  handleRemoveVideo = (id) => {
    this.setState((prevState) => ({
      videoData: prevState.videoData.filter((video) => video.id !== id),
    }));
  };

  toggleForm = () => {
    this.setState((prevState) => ({
      showForm: !prevState.showForm,
    }));
  };

  render() {
    const { videoData, showForm } = this.state;

    return (
      <div className="app">
        <h1>Video App</h1>
        <div className="video-container">
          {videoData.map((video) => (
            <Video
              key={video.id}
              id={video.id}
              title={video.title}
              url={video.url}
              votes={video.votes}
              handleVote={this.handleVote}
              handleRemoveVideo={this.handleRemoveVideo}
            />
          ))}
        </div>
        <div className="button-container">
          <button className="add-button" onClick={this.toggleForm}>
            {showForm ? "Cancel" : "Add Video"}
          </button>
          {showForm && <AddVideoForm handleAddVideo={this.handleAddVideo} />}
        </div>
      </div>
    );
  }
}

export default App;
