import React from 'react';

class Downvote extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            count: 0
        }
    }

    decrement = () => {

        this.setState({
            count: this.state.count - 1
        })
    }

    render() {
        return (
            <div>
                <div>
                <i onClick={this.decrement} style={{ fontSize: '24px' }} className="fa">&#xf088;</i>
                <span>{this.state.count}</span>
                </div>
            </div>
        );
    }
}

export default Downvote;
