import React, { useState } from "react";

function AddVideo({ onAddVideo }) {
  const [title, setTitle] = useState("");
  const [url, setUrl] = useState("");

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleUrlChange = (event) => {
    setUrl(event.target.value);
  };

  const handleAddVideo = () => {
    // Validate that both title and URL are provided
    if (title.trim() === "" || url.trim() === "") {
      alert("Please enter both title and URL.");
      return;
    }

    // Create a new video object
    const newVideo = {
      id: Date.now(), // Generate a unique ID (you can use a library for this)
      title,
      url,
      rating: 0, // Initialize the rating to 0
    };

    // Call the parent component's function to add the video
    onAddVideo(newVideo);

    // Clear the input fields
    setTitle("");
    setUrl("");
  };

  return (
    <div className="AddVideo">
      <h2>Add a Video</h2>
      <label htmlFor="title">Title:</label>
      <input
        type="text"
        id="title"
        value={title}
        onChange={handleTitleChange}
      />
      <label htmlFor="url">URL:</label>
      <input type="text" id="url" value={url} onChange={handleUrlChange} />
      <button onClick={handleAddVideo}>Add Video</button>
    </div>
  );
}

export default AddVideo;
