import React from "react";
import { useState } from "react";

function Video(prop) {
  const videoId = getVideoIdFromUrl(prop.videoObj.url);

  const [title, setTitle] = useState("");
  const [url, setUrl] = useState("");

  const titleChange = (event) => {
    setTitle(event.target.value);
  };

  const urlChange = (event) => {
    setUrl(event.target.value);
  };
  return (

    <div class="card">
      <iframe
        src={`https://www.youtube.com/embed/${videoId}`}
        frameborder="0"
        allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
        title="YouTube video player"
        allowfullscreen height={'300px'}
      ></iframe>
      <div class="card-body">
        <h5 class="card-title">{prop.videoObj.title}</h5>
        <p class="card-text">Requasted At :</p>
        <div className="buttons">
          <button type="button" class="btn btn-primary"> &#128077; </button>
          <p>{prop.videoObj.rating} </p>
          <button type="button" class="btn btn-warning"> &#128078; </button>
          <button type="button" class="btn btn-danger">
            <i class="fa fa-trash-o"></i>
          </button>
        </div>
      </div>
    </div>
  );
}
export default Video;
function getVideoIdFromUrl(url) {
  const videoId = url.split("v=")[1];
  return videoId;
}
