import React, { useState } from "react";
import SearchBar from "./SearchBar";
import SingleVideoCard from "./SingleVideoCard";
import exampleResponse from "./exampleresponse.json";

function VideoCards() {
  const [filterVideos, setFilterVideos] = useState(exampleResponse);
  const [searchedVideo, setSearchedVideo] = useState(exampleResponse);

  function handleDelete(id) {
    setFilterVideos((prevVideo) => {
      return prevVideo.filter((v) => v.id !== id);
    });
  }

  function search(searchValue) {
    setSearchedVideo(
      filterVideos.filter((item) =>
        item.title.toLowerCase().includes(searchValue.title.toLowerCase())
      )
    );
  }

  return (
    <>
      <SearchBar search={search} />
      {filterVideos.map((video) => (
        <SingleVideoCard
          key={video.id}
          title={video.title}
          url={video.url}
          deleteVideo={() => handleDelete(video.id)}
        />
      ))}
    </>
  );
}

export default VideoCards;
