import React, { useEffect, useState } from "react";
import "./App.css";

//console.log(dataVideo)
function VideoComponent({ elements }) {
  return (
    <div>
      <div id="ascendingButton">
        <button class="btn btn-light customBackground1">Ascending</button>
        <button class="btn btn-light customBackground1">Descending</button>
      </div>

      <div class="container">
        <div class="videoDisplayContainer">
          {elements.map((element, index) => (
            <VideoDisplay key={index} element={element} />
          ))}
        </div>
      </div>
    </div>
  );
}
function VideoDisplay({ element }) {
  const [score, setScore] = useState(element.rating);
  const [videoData, setVideoData] = useState([]);

  useEffect(() => {
    fetch("/videos")
      .then((response) => response.json())
      .then((updatedData) => {
        setVideoData(updatedData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);
  ///***************likedislike****************** */
  const handleLike = (videoId) => {
    // Update frontend state optimistically
    const updatedVideos = videoData.map((video) => {
      if (video.id === videoId) {
        return { ...video, rating: video.rating + 1 };
      }
      return video;
    });
    setVideoData(updatedVideos);

    // Send API request to update card's vote count
    fetch(`/videos/${videoId}/like`, { method: "POST" });
  };

  const handleDislike = (videoId) => {
    // Update frontend state optimistically
    const updatedVideos = videoData.map((video) => {
      if (video.id === videoId) {
        return { ...video, rating: Math.max(video.rating - 1, 0) };
      }
      return video;
    });
    setVideoData(updatedVideos);

    // Send API request to update card's vote count
    fetch(`/videos/${videoId}/dislike`, { method: "POST" });
  };

  ///********************************* */
  const handleDelete = async (id) => {
    try {
      const response = await fetch(`/videos/${id}`, { method: "DELETE" });
      if (response.ok) {
        setVideoData(videoData.filter((element) => element.id !== id));
      } else {
        console.error("Error deleting video:");
      }
    } catch (error) {
      console.error("Error deleting video:", error);
    }
  };

  return (
    <div class="displayGrid">
      <h5>{element.title}</h5>

      <iframe
        width="330"
        height="200"
        src={element.url}
        title={element.title}
        border="none"
        border-radius="103px"
        frameborder="0"
        allowfullscreen
        align="center"
      ></iframe>
      <div class="likedislikeBtn">
        <button
          videoId={element.id}
          class="btn btn-light customBackground5"
          onClick={handleLike /*() => setScore((s) => s + 1)*/}
        >
          Like
        </button>
        <span>Votes:&nbsp;{element.rating}</span>

        <button
          videoId={element.id}
          class="btn btn-light customBackground5"
          onClick={handleDislike /*() => setScore((s) => s - 1)*/}
        >
          Dislike
        </button>
      </div>
      <button
        class="btn btn-light deleteBtn"
        onClick={() => handleDelete(element.id)}
      >
        Delete
      </button>
    </div>
  );
}

export default function AllVideoDisplay() {
  const [videoData, setVideoData] = useState([]);

  useEffect(() => {
    fetch("/videos")
      .then((response) => response.json())
      .then((updatedData) => {
        setVideoData(updatedData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  return (
    <VideoComponent elements={videoData.sort((a, b) => b.rating - a.rating)} />
  );
}
