import React, { useState } from "react";
import "../index.css";

const AddVideo = ({ onVidAdd }) => {
  const [title, setTitle] = useState('');
  const [url, setUrl] = useState('');

  const titleChange = (event) => {
    setTitle(event.target.value);
  };

  const urlChange = (event) => {
    setUrl(event.target.value);
  };

  const newVid = () => {
    const newInput = { title, url };
    onVidAdd(newInput);

    setTitle('');
    setUrl('');
  };

  return (
    <form>
     <div class="form-group row">
         <label for="colFormLabelSm" class="col-sm-2 col-form-label col-form-label-sm">
           Title
         </label>
         <div class="col-sm-10">
           <input type="text" value={title} onChange={titleChange} class="form-control form-control-sm" id="colFormLabelSm" placeholder="Title" />
         </div>
       </div>
       <div class="form-group row">
         <label for="colFormLabelSm" class="col-sm-2 col-form-label col-form-label-sm">
           URL
         </label>
         <div class="col-sm-10">
           <input type="text" value={url} onChange={urlChange} class="form-control form-control-sm" id="colFormLabelSm" placeholder="URL" />
         </div>
       </div>
      <button onClick={newVid} type="button" className="btn btn-success">
        Add
      </button>
      <button type="button" class="btn btn-warning">
         Cancel
//       </button>
    </form>
  );
};

export default AddVideo;
