import Rating from "./Rating";
import React, { useState, useEffect } from "react";

const VideoComponent = () => {
const [videos, setVideos] = useState([]);
   const [filteredVideos, setFilteredVideos] = useState([]);
   const [videoList, setVideoList] = useState([]);
   const [sortedVideoList, setSortedVideoList] = useState([]);
  // const [ascending, setAscending] =useState(false)



   const searchVideos = (searchTerm) => {
     const filtered = videos.filter((video) =>
       video.title.toLowerCase().includes(searchTerm.toLowerCase())
     );
     setFilteredVideos(filtered);
   };
    // const removeVideo = (videoId) => {
    //   const updatedVideoList = videoList.filter(
    //     (video) => video.id !== videoId
    //   );
    //   setVideos(updatedVideoList);
    //   setSortedVideoList(
    //     [...updatedVideoList].sort((a, b) => b.rating - a.rating)
    //   );
    // };


  async function fetchData() {
    try {
      const url = "http://localhost:5000/videos";
      const fetchURL = `${url}`;
      // `http://localhost:5000/videos`;
      const response = await fetch(fetchURL );
      if(response.ok){
        console.log(response);
        const data = await response.json();
        console.log(data);
        setVideos(data.videos);
        const sortedList = [...(data.videos)].sort((a, b) => b.rating - a.rating);
        setSortedVideoList(sortedList);
      }
    } catch (error) {

      console.log(error);
    }
  }


  useEffect(() => {
    fetchData();
    console.log("useEffect");
  }, []);

  const addVideo = (newVideo) => {
    const time = moment().format("YYYY-MM-DD HH:mm");
    const addnewVideo = [...videos, { ...newVideo, rating: 0, time }];
    console.log(addnewVideo);
    setVideos(addnewVideo);
    setSortedVideoList([...addnewVideo].sort((a, b) => b.rating - a.rating));
  };
     const displayedVideos =
       filteredVideos.length > 0 ? filteredVideos : videos;

  // function handleDeleteVideo(videoID) {

  //   fetch(`http://localhost:5000/video/${videoID}`, {
  //     method: "DELETE",
  //     headers: {
  //       Content_Type: "application/json",
  //     },
  //   })
  //     .then((response) => {
  //       if (!response.ok) {
  //         throw new Error(`${videoID} could not be found`);
  //       }
  //       console.log(response);
  //       getAllVideos();
  //     })
  //     .catch((error) => console.log(error));
  // }



  const handleDelete = (id) => {
    setVideos(videos.filter((video) => video.id !== id));
  };
  return (
    <div>
      {sortedVideoList.map((video, index) => (
        <div key={index} className="video-card">
          <h3>{video.title}</h3>
          <iframe
            width="560"
            height="315"
            src={videos.Url}
            title={videos.title}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
          <Rating rating={videos.rating} />
          <button onClick={() => handleDelete(video.id)}>Remove</button>
        </div>
      ))}
    </div>
  );
};

export default VideoComponent;
