import { useState, useEffect } from "react";
import "./App.css";
import Video from "./Video";
import Button from "./components/Button";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";

function App() {
  const [video, setVideo] = useState([]);

  const addVideo = (title, url) => {
    
    setVideo([...videos, { title, url, votes: 0, id: video.length }]);
  };

  const removeVideo = async (id) => {
    const res = await fetch(`http://localhost:5000/${id}`, {
      method: "DELETE",
    });
    await res.json();
    setVideos(video.filter((videos) => videos.id !== id));
  };

  useEffect(() => {
    async function getVideo() {
      const response = await fetch("http://localhost:5000");
      const data = await response.json();
      console.log(data);

      setVideo([...data]);
    }

    getVideo();
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>React Video Recommendation Engine</h1>
        <Button onVideoAdded={addVideo} />
      </header>
      <hr />
      <Container className="container">
        {video.map((video) => (
          <Col>
            <Videoszz className="video"
              key={video.id}
              id={video.id}
              title={video.title}
              url={
                "https://youtube.com/embed/" +
                video.url
                  .match(/v=([^&]+)/g)
                  .join()
                  .slice(2)
              }
              votes={video.votes}
              onVideoRemoved={removeVideo}
            />
          </Col>
        ))}
      </Container>
      <hr />

      <Video />
    </div>
  );
}

export default App;
