import "./App.css";
import React, { useState } from 'react';
function Uploadvideo() {
  const [showForm, setShowForm] = useState(false);

  const handleButtonClick = () => {
    if (!showForm) {
      setShowForm(true);
    }

  };
  const handleCancelButtonClick = () => {
    if (showForm) {
      setShowForm(false);
    }

  };


  return (

      <div><form>
        <button class="uploadVideo_btn" onClick={handleButtonClick}>

        <a href="#0">Upload Video</a>
      </button>
      {showForm && (
        <div class="container"> <form id="form">
        <div>
          <label>Title:</label>
          <input type="text" class="input" name="title" required></input>
        </div>
        <div>
          <label for="input">url:&nbsp;&nbsp;&nbsp;</label>
          <input id="input"class="input" type="text"  name="title" required></input>
        </div>
        <div class="formBtn">
          <button class="cancelBtn" onClick={handleCancelButtonClick}>Cancel</button>
          <button class="uploadBtn">Upload</button>
        </div>
      </form></div>

      )}

        </form></div>

  );
}
function Searchvideo() {
  return (
    <div>
      <label>Search:</label>
      <input type="text" class="searchVideo" name="search"></input>
    </div>
  );
}

export default function Search() {
  return (
    <div class="search">
      <Uploadvideo />
      <Searchvideo />
    </div>
  );
}
